site.headStyle:
.silex-container-content {}

.shadow {}

.oswald.text-element,
.oswald .text-element {
  font-family: 'Oswald', sans-serif;
}

.abel.text-element,
.abel .text-element {
  font-family: 'Abel', sans-serif;
}

.links a,
a {
  color: #006CB2;
  text-decoration: none;
}

td,
tr,
th {
  border: 1px solid;
}

.text-element HEADER,
.text-element H1,
.text-element H2,
.text-element H3,
.text-element DIV,
.text-element P,
.text-element QUOTE {}

.very-big {
  color: #FF3200;
}

.text-element HEADER {
  font-size: 45px;
}

.background {}

.xnormal {
  text-align: justify;
}

.text-element.very-big * {
  font-size: 50px;
  line-height: 2.5;
  font-weight: 100;
}

.text-element.special-format .normal {
  font-size: 55px;
  color: white;
  font-family: 'Oswald', sans-serif;
  line-height: 57px;
}

.special-format2 .normal {
  text-transform: uppercase;
  font-size: 74px;
  line-height: 64px;
}

.special-format2.verybig * {
  font-size: 180px;
  line-height: 180px;
}

.special-format2 h2,
.special-format2 h1,
.special-format2 p {
  margin: 0px;
}

.special-format2 .heading2 {
  letter-spacing: 10px;
  line-height: 0;
}

.special-format2 .heading1 {
  font-size: 30px;
  word-spacing: 1px;
  text-align: left;
  line-height: 30px;
}

.special-format2 .title {
  font-size: 60px;
  line-height: 54px;
  word-spacing: 1px;
}

.text-shadow {
  text-shadow: -1px 2px #FF3200;
}

div.full-width {
  width: 100%;
}

.centered {
  margin: auto;
  position: relative;
}

.back {
  z-index: -1;
}

.fixed-menu .text-element {
  padding: 0px;
  min-height: 35px;
  top: 0px;
  line-height: 1.8;
  font-size: 18px;
  color: #FFFFFF;
  text-align: center;
}

.silex-runtime .fixed-menu {
  position: fixed;
  z-index: 120;
}

.active-menu {
  border-bottom: 3px solid #006CB2;
}

.silex-runtime .discret li,
.silex-runtime .discret .heading2 {
  color: black !important;
  font-size: 14px !important;
  float: left !important;
  list-style-type: none;
  margin-left: 5px;
}

.discret H1 {
  color: black !important;
  font-size: 16px !important;
}

.github-issues {
  overflow-x: hidden;
  overflow-y: auto;
}

.showcase-list.github-issues {
  overflow: hidden;
}

.github-issues ul {
  position: absolute;
}

.github-issues .silex-element-content {
  overflow: hidden;
}

.github-issues ul {
  margin: 0;
  padding: 0;
}

.github-issues li {
  list-style-type: none;
  margin: 0;
  padding: 3px;
  border-bottom: 1px solid #cccccc;
  font-size: 14px;
  line-height: 19px;
}

.github-issues li a {
  text-decoration: none;
  color: black;
}

.github-issues-image ul {
  text-align: center;
}

.github-issues-image li {
  display: inline;
}

.github-issues-image img {
  max-width: 45%;
  border-radius: 5px;
}

.github-issues-image.showcase-list img {
  max-width: 42%;
  border-radius: 5px;
}

.github-issues.github-issues-white * {
  color: white;
  text-align: center;
}

.github-issues.github-issues-white li {
  border-bottom-color: #999999;
}

.github-issues.github-issues-image li {
  border-bottom: none;
}

.share-buttons {
  list-style: none;
}

.share-buttons li {
  display: inline;
}

.share-buttons a {
  text-decoration: none;
}

.poll-bg {
  position: fixed;
  top: 0 !important;
  left: 0 !important;
  width: 100% !important;
  height: 100% !important;
  z-index: 100;
}

.page-poll-opened .background {
  -webkit-filter: blur(5px);
  -moz-filter: blur(5px);
  -o-filter: blur(5px);
  -ms-filter: blur(5px);
  filter: blur(5px);
}

.silex-runtime .poll-bg {
  background-color: transparent;
}

.poll-center {
  position: relative;
  left: -320px !important;
  margin-left: 640px;
  top: -250px !important;
  margin-top: 500px;
}

.editable-style.forkme {
  right: 0;
  top: 0;
  left: auto;
  z-index: 200;
  position: fixed;
}

@media (max-width:480px) {
  .editable-style.fixed-info {
    display: none;
  }
}

@media (min-width:481px) {
  html>.editable-style.fixed-info {
    opacity: 0;
    bottom: -600px;
    transition: opacity .2s ease-in, bottom .2s ease-in;
    top: unset;
    position: fixed;
  }

  .silex-editor .fixed-info,
  html.show-info>.editable-style.fixed-info {
    opacity: 1;
    bottom: 0;
    z-index: 100;
  }

  .silex-editor .fixed-info {
    bottom: unset;
    position: absolute;
  }
}

.editable-style.fixed-info {
  box-shadow: 0 3px 6px rgba(0, 0, 0, .16), 0 3px 6px rgba(0, 0, 0, .23);
}

.editable-style.fixed-info .button p {
  margin: 0px;
  text-align: center;
}

.editable-style.fixed-info .button {
  width: 40px;
  min-height: 40px;
  height: 40px;
  cursor: pointer;
}

.limit-height {
  height: 500px;
}

.silex-pages {
  display: none;
}

body.enable-mobile {
  padding-top: 0;
}

.header h1 {
  letter-spacing: 12px;
  font-family: Abel, sans-serif;
}

.header h2 {
  font-size: 3.3em;
  font-family: 'Oswald', sans-serif;
  color: #FF0;
  font-weight: 200;
  text-transform: uppercase;
  text-align: center;
  letter-spacing: 12px;
  height: 115px;
  line-height: 1.3;
}

.header-slideshow {
  height: 320px;
}

.element2 {
  line-height: 18px;
  margin-left: 10px;
}

